        .main {
            flex: 1;
            display: flex;
            overflow: hidden;
            margin-top: 1rem;
            width: 100vw;
        }


        /* Content wrapper */
        .content-wrapper {
            flex: 3;
            display: flex;
            flex-direction: column;
            background-color: #fff5f6;
            margin-right: 20px;
        }

        /* Scrollable content */
        .content {
            flex: 1;
            padding: 2rem;
            padding-top: 4rem;
            overflow-y: auto;
